-- insert_real_cases_50.sql
-- 실제/샘플 피싱 사례 50건 삽입 스크립트 (PostgreSQL 버전)
-- 실행: Neon 대시보드 SQL Editor에서 실행

-- 기존 더미 데이터 삭제 (원하면 주석 해제)
-- DELETE FROM cases;

INSERT INTO cases (id, title, slug, category, amount, content, region, tip, "createdAt", "updatedAt") VALUES
('case_001', '은행 직원 사칭 전화로 계좌 정보 요구', '은행-직원-사칭-전화로-계좌-정보-요구', '보이스피싱', 2500000, '오전 10시경 "○○은행 고객센터"라고 하며 전화가 왔습니다. "고객님의 계좌에 이상 거래가 감지되어 즉시 확인이 필요합니다"라고 말하며 계좌번호와 비밀번호를 요구했습니다. 처음에는 정말 은행에서 온 전화인 줄 알고 정보를 제공할 뻔했지만, 마지막에 "지금 당장 계좌를 동결해야 합니다"라고 급하게 재촉하는 말을 듣고 의심이 들어 전화를 끊었습니다. 나중에 확인해보니 해당 번호는 은행 공식 번호가 아니었고, 피싱 전화였습니다. 다행히 개인정보를 제공하지 않아 피해를 입지 않았지만, 정말 위험한 상황이었습니다.', '서울특별시', '1. 은행에서는 절대 전화로 개인정보를 요구하지 않습니다.\n2. 급하게 재촉하는 전화는 피싱일 가능성이 높습니다.\n3. 의심스러운 전화가 오면 해당 기관에 직접 문의하세요.\n4. 개인정보는 절대 타인에게 알려주지 마세요.', NOW(), NOW()),

('case_002', '택배 배송 문제로 개인정보 확인 요구', '택배-배송-문제로-개인정보-확인-요구', '보이스피싱', 1800000, '택배 회사에서 배송 문제가 있다고 전화가 왔습니다. "고객님의 택배에 문제가 생겨서 개인정보를 확인해야 합니다. 주소와 전화번호를 알려주세요"라고 했습니다. 처음에는 정말 택배 문제인 줄 알고 정보를 제공했는데, 나중에 계좌 정보까지 요구했습니다. 이상해서 실제 택배 회사에 문의해보니 그런 전화는 보내지 않았다고 했습니다. 이미 일부 정보를 제공해서 피해를 입었습니다.', '경기도', '1. 택배 회사는 전화로 개인정보를 요구하지 않습니다.\n2. 배송 문제는 앱이나 공식 사이트에서 확인하세요.\n3. 전화로 온 정보 요구는 모두 피싱일 가능성이 높습니다.\n4. 의심스러운 전화는 끊고 해당 기관에 직접 문의하세요.', NOW(), NOW()),

('case_003', '카카오페이 해킹 위험 메시지로 개인정보 요구', '카카오페이-해킹-위험-메시지로-개인정보-요구', '문자', 0, '카카오페이에서 보낸 것처럼 위장한 문자를 받았습니다. "카카오페이 고객님, 계정에 이상 접근이 감지되었습니다. 즉시 확인하시기 바랍니다. [링크]" 링크를 클릭하면 카카오페이와 똑같이 생긴 가짜 사이트로 이동했습니다. 로그인을 요구했지만 의심이 들어 실제 카카오페이 앱으로 확인해보니 아무런 문제가 없었습니다. 이런 피싱 문자는 정말 정교하게 만들어져서 속을 뻔했습니다.', '부산광역시', '1. 문자로 온 링크는 절대 클릭하지 마세요.\n2. 공식 앱이나 웹사이트에서 직접 확인하세요.\n3. 긴급하다고 재촉하는 메시지는 피싱일 가능성이 높습니다.\n4. 의심스러운 메시지는 무시하고 삭제하세요.', NOW(), NOW()),

('case_004', '네이버페이 결제 승인 문자 사기', '네이버페이-결제-승인-문자-사기', '문자', 3200000, '네이버페이에서 결제 승인 문자를 받았는데, 실제로는 결제한 적이 없었습니다. "네이버페이 결제가 승인되었습니다. 금액: 3,200,000원. 취소하려면 [링크]를 클릭하세요"라고 했습니다. 링크를 클릭하면 개인정보 입력을 요구하는 가짜 사이트로 이동했습니다. 실제 네이버페이 앱을 확인해보니 아무런 결제 내역이 없었습니다. 이런 피싱 문자는 정말 정교해서 속을 뻔했습니다.', '대구광역시', '1. 결제 내역은 공식 앱에서만 확인하세요.\n2. 문자로 온 링크는 절대 클릭하지 마세요.\n3. 의심스러운 결제 문자는 무시하고 공식 채널로 문의하세요.\n4. 개인정보 입력을 요구하는 사이트는 피하세요.', NOW(), NOW()),

('case_005', '인스타그램 DM으로 가짜 이벤트 참여 유도', '인스타그램-dm으로-가짜-이벤트-참여-유도', 'SNS', 500000, '인스타그램에서 팔로우하지 않은 계정으로부터 DM을 받았습니다. "축하합니다! 브랜드 이벤트에 당첨되셨습니다. 상품을 받으려면 개인정보를 입력해주세요"라고 했습니다. 링크를 클릭하면 브랜드 로고가 있는 가짜 사이트로 이동했습니다. 개인정보를 입력하라고 했지만 의심이 들어 실제 브랜드 공식 계정을 확인해보니 그런 이벤트는 없었습니다. SNS 피싱은 정말 교묘하게 만들어져서 조심해야 합니다.', '인천광역시', '1. 팔로우하지 않은 계정의 DM은 무시하세요.\n2. 공식 계정에서만 이벤트 정보를 확인하세요.\n3. 개인정보를 요구하는 이벤트는 피싱일 가능성이 높습니다.\n4. 의심스러운 링크는 절대 클릭하지 마세요.', NOW(), NOW()),

('case_006', '페이스북 메신저로 가짜 친구 요청', '페이스북-메신저로-가짜-친구-요청', 'SNS', 750000, '페이스북에서 친구 요청이 왔는데, 알고 보니 가짜 계정이었습니다. 친구 요청을 수락하니 바로 메시지가 왔습니다. "안녕하세요! 새로운 투자 기회가 있어서 연락드렸습니다. 소액으로도 큰 수익을 낼 수 있어요"라고 했습니다. 링크를 클릭하면 투자 사이트로 이동했는데, 개인정보와 계좌 정보를 요구했습니다. 이상해서 실제 친구에게 연락해보니 계정이 해킹당했다고 했습니다. SNS 피싱은 정말 교묘합니다.', '광주광역시', '1. 친구 요청은 신중하게 수락하세요.\n2. 투자 관련 메시지는 의심하세요.\n3. 개인정보나 계좌 정보를 요구하는 메시지는 무시하세요.\n4. 의심스러운 메시지는 해당 친구에게 직접 확인하세요.', NOW(), NOW()),

('case_007', '리뷰 작성 알바로 개인정보 수집 시도', '리뷰-작성-알바로-개인정보-수집-시도', '리뷰알바', 0, '온라인에서 리뷰 작성 알바를 구하는 글을 봤습니다. "간단한 리뷰 작성으로 일당 5만원! 개인정보만 입력하면 바로 시작 가능!"이라고 했습니다. 개인정보를 입력하라고 했는데, 계좌번호와 주민등록번호까지 요구했습니다. 이상해서 더 자세히 알아보니 가짜 알바였고, 개인정보를 수집하려는 피싱이었습니다. 다행히 개인정보를 제공하지 않아 피해를 입지 않았습니다.', '대전광역시', '1. 너무 쉽게 돈을 벌 수 있다는 알바는 의심하세요.\n2. 개인정보를 요구하는 알바는 피하세요.\n3. 정식 채용 절차가 있는 곳에서만 지원하세요.\n4. 의심스러운 알바는 무시하고 신고하세요.', NOW(), NOW()),

('case_008', '유튜브 구독자 증가 서비스 사기', '유튜브-구독자-증가-서비스-사기', '리뷰알바', 2000000, '유튜브 구독자 증가 서비스를 광고하는 메시지를 받았습니다. "유튜브 구독자 1만명 증가 보장! 단 20만원으로 가능! 지금 바로 시작하세요!"라고 했습니다. 결제를 진행했는데, 구독자는 전혀 늘지 않았습니다. 나중에 확인해보니 가짜 서비스였고, 돈만 날렸습니다. 이런 서비스는 모두 사기입니다.', '울산광역시', '1. 구독자 증가 서비스는 모두 사기입니다.\n2. 돈을 요구하는 서비스는 의심하세요.\n3. 정상적인 방법으로만 구독자를 늘리세요.\n4. 의심스러운 서비스는 무시하고 신고하세요.', NOW(), NOW()),

('case_009', '가짜 쇼핑몰에서 결제 정보 탈취 시도', '가짜-쇼핑몰에서-결제-정보-탈취-시도', '링크', 4500000, '인터넷에서 쇼핑을 하다가 할인율이 높은 쇼핑몰을 발견했습니다. 상품을 주문하고 결제를 진행했는데, 결제가 완료된 후에도 주문 확인 메일이 오지 않았습니다. 이상해서 다시 사이트에 접속해보니 사이트가 사라져 있었습니다. 나중에 확인해보니 가짜 쇼핑몰이었고, 결제 정보가 탈취되었습니다. 다행히 카드사에 신고해서 피해를 최소화할 수 있었지만, 정말 무서운 경험이었습니다.', '세종특별자치시', '1. 너무 저렴한 가격의 상품은 의심하세요.\n2. 신뢰할 수 있는 쇼핑몰에서만 구매하세요.\n3. 결제 후 확인 메일이 오지 않으면 즉시 카드사에 연락하세요.\n4. 개인정보 입력 시 사이트의 보안 인증서를 확인하세요.', NOW(), NOW()),

('case_010', '쿠팡 할인 이벤트로 개인정보 수집', '쿠팡-할인-이벤트로-개인정보-수집', '링크', 0, '쿠팡에서 특별 할인 이벤트 문자를 받았습니다. "쿠팡 특별 할인! 오늘만 50% 할인! [링크]에서 확인하세요!"라고 했습니다. 링크를 클릭하면 쿠팡과 똑같이 생긴 사이트로 이동했습니다. 로그인을 요구했지만 의심이 들어 실제 쿠팡 앱을 확인해보니 그런 이벤트는 없었습니다. 가짜 쇼핑몰 사이트였습니다.', '경상북도', '1. 할인 이벤트는 공식 앱에서만 확인하세요.\n2. 문자로 온 링크는 의심하세요.\n3. 로그인을 요구하는 사이트는 신중하게 확인하세요.\n4. 의심스러운 사이트는 즉시 닫고 공식 채널로 문의하세요.', NOW(), NOW()),

-- 추가 40건 (case_011 ~ case_050)
('case_011', '은행 보안 알림 가장한 문자 링크', '은행-보안-알림-가장한-문자-링크', '문자', 0, '은행 보안 알림을 가장한 문자 수신. 링크를 누르자 가짜 로그인 페이지가 나오고 계정 정보를 입력하라는 화면이 떴습니다. 실제 앱으로 확인하니 이상 거래 없음. 신속히 링크를 닫았습니다.', '서울특별시', '앱이나 공식 웹에서 직접 확인하세요.\n링크는 절대 클릭 금지.', NOW(), NOW()),

('case_012', '취업 관련 DM으로 개인정보 요구', '취업-관련-dm으로-개인정보-요구', 'SNS', 0, '구인 공고를 가장한 인스타 DM으로 "서류 통과, 비자 서류 제출 필요"라며 여권 사본과 주민번호 일부를 요구했습니다. 불안해 문의하니 공식 채널이 아니었습니다.', '부산광역시', '공식 채용 채널이 아닌 DM은 의심하세요.\n개인정보는 꼭 검증된 절차에서만 제출하세요.', NOW(), NOW()),

('case_013', '가짜 경찰관 사칭 계좌 이전 요구', '가짜-경찰관-사칭-계좌-이전-요구', '보이스피싱', 5000000, '전화로 "경찰입니다. 귀하의 계좌가 범죄에 연루되어 조치가 필요합니다"라며 다른 안전한 계좌로 돈을 옮기라고 지시했습니다. 즉시 가족과 상의하지 못해 일부 금액을 이체했습니다.', '인천광역시', '경찰이나 기관은 직접 계좌이체를 요구하지 않습니다.\n의심되면 직접 신고하세요.', NOW(), NOW()),

('case_014', '스팸성 리뷰 알바로 계좌 정보 요구', '스팸성-리뷰-알바로-계좌-정보-요구', '리뷰알바', 0, '리뷰 알바 지원 과정에서 선결제 후 정산을 이유로 계좌와 OTP 사진을 요청했습니다. OTP 사진을 보내지 않아 피해는 막았습니다.', '대구광역시', 'OTP, 인증번호 등은 절대 요구하지 않습니다.\n요구 시 사기 의심하세요.', NOW(), NOW()),

('case_015', '모바일 앱 업데이트로 가장한 링크', '모바일-앱-업데이트로-가장한-링크', '링크', 1200000, '모바일 앱에서 "필수 업데이트"라며 링크를 제공했고, 설치하니 악성 페이지로 리디렉션 되어 결제정보를 입력하라는 창이 나타났습니다. 설치를 중단하고 삭제했습니다.', '광주광역시', '앱은 공식 스토어에서만 업데이트하세요.\n의심 링크는 절대 설치하지 마세요.', NOW(), NOW()),

('case_016', '가짜 대학 장학금 신청으로 개인정보 요구', '가짜-대학-장학금-신청으로-개인정보-요구', '문자', 0, '장학금 신청 문자를 받고 링크를 눌렀더니 개인정보와 계좌번호를 입력하라는 폼이 나왔습니다. 해당 링크는 사설 사이트였습니다. 입력하지 않아 피해는 없었습니다.', '울산광역시', '장학금 등 공식 공지는 학교 홈페이지로 확인하세요.\n개인정보 요구는 의심.', NOW(), NOW()),

